"use strict";$(document).ready(function(){$("[href]").each(function(){this.href==window.location.href&&$(this).addClass("link--active")}),$(".dropdown").each(function(){var d=this;$(this).children(".dropdown__header").click(function(){$(d).children(".dropdown__header").children(".dropdown__arrow").toggleClass("rotated"),$(d).children(".dropdown__content").slideToggle()})})});